import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 

const UploadHouseForm = () => {
  const navigate = useNavigate();
  const [selectedTags, setSelectedTags] = useState([]);
  const [formData, setFormData] = useState({
    structureId: '',
    structureType: 'House',
    userId: 'User1',
    images: [],
    tags: '',
    files: []
  });

  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData({
      ...formData,
      images: files
    });
  };

  const handleTagChange = (e) => {
    const { value } = e.target;
    setSelectedTags(Array.from(e.target.selectedOptions, option => option.value));
    setFormData({
      ...formData,
      tags: selectedTags.join(', '), // Update the tags field in formData
    });
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    const allowedExtensions = ['.pdf', '.obj', '.stl'];
    const validFiles = files.filter(file => {
      const extension = file.name.split('.').pop();
      return allowedExtensions.includes(`.${extension.toLowerCase()}`);
    });

    if (validFiles.length !== files.length) {
      setError('Please upload files with .pdf, .obj, or .stl extensions only.');
    } else {
      setFormData(prevFormData => ({
        ...prevFormData,
        files: [...prevFormData.files, ...validFiles]
      }));
      setError(null);
    }
  };  

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { structureId, userId, images, tags, files } = formData;

    if (!structureId || !userId || images.length === 0 || !files.length) {
      setError('Please fill in all required fields.');
      setEmptyFields(['structureId', 'userId', 'images', 'files']);
      return;
    }

    // Handle form submission, e.g., send formData to backend
    console.log(formData);

    // Clear form data after submission if needed
    setFormData({
      structureId: '',
      structureType: 'house',
      userId: 'User1',
      images: [],
      tags: '',
      files: []
    });

    setError(null);
    setEmptyFields([]);
  };

  const handleCancel = () => {
    navigate('/'); // Navigate back to the home page
  };

  return (
    <div className="form-container">
    <form className="create" onSubmit={handleSubmit}> 
      <h3>Upload House Model</h3>

      <label htmlFor="structureType">Structure Type</label>
      <input 
        type="text" 
        id="structureType" 
        name="structureType" 
        value={formData.structureType} 
        readOnly // Prevent editing
      />

      <label htmlFor="userId">User ID</label>
      <input 
        type="text" 
        id="userId" 
        name="userId" 
        value={formData.userId} 
        readOnly // Prevent editing
      />

      <label htmlFor="images">Images</label>
      <input 
        type="file" 
        id="images" 
        name="images" 
        onChange={handleImageChange} 
        multiple 
        accept="image/*" 
        className={emptyFields.includes('images') ? 'error' : ''}
      />

      <label htmlFor="tags">Tags</label>
      <select 
        id="tags" 
        name="tags" 
        value={formData.tags} 
        onChange={handleChange} 
        className={emptyFields.includes('tags') ? 'error' : ''}
        multiple
        >
        <option value="">Select a tag</option>
        <option value="residential">Residential</option>
        <option value="business">Business</option>
        <option value="commercial">Commercial</option>
        <option value="farm">Farm</option>
        <option value="infrastructure">Infrastructure</option>
        <option value="sustainable">Sustainable</option>
        <option value="tiny house">Tiny House</option>
        <option value="single-story">Single-Story</option>
        <option value="two-story">Two-Story</option>
        <option value="hut">Hut</option>
        <option value="component">Component</option>
        <option value="decorative">Decorative</option>
        <option value="retaining">Retaining</option>
        <option value="earthquake">Earthquake</option>
        <option value="hurricane">Hurricane</option>
        <option value="Tudor">Tudor</option>
        <option value="cape cod">Cape Cod</option>
        <option value="Mediterranean">Mediterranean</option>
        <option value="southwest">Southwest</option>
        <option value="modern">Modern</option>
        <option value="craftsman">Craftsman</option>
        <option value="contemporary">Contemporary</option>
        <option value="specialty">Specialty</option>
        <option value="industrial">Industrial</option>
        <option value="beach">Beach</option>
        <option value="bungalow">Bungalow</option>
        <option value="cabin">Cabin</option>
        <option value="colonial">Colonial</option>
        <option value="classical">Classical</option>
        <option value="farmhouse">Farmhouse</option>
        <option value="ranch">Ranch</option>
        <option value="prairie">Prairie</option>
        <option value="southern">Southern</option>
        <option value="Victorian">Victorian</option>
      </select>

      <label htmlFor="files">Files</label>
      <input 
        type="file" 
        id="files" 
        name="files" 
        onChange={handleFileChange} 
        multiple 
        className={emptyFields.includes('files') ? 'error' : ''}
      />

      <div>
        <button type="submit">Submit</button>
        <button type="button" onClick={handleCancel}>Cancel</button>
      </div>

      {error && <div className="error">{error}</div>}

      {formData.files.length > 0 && (
        <div>
          <h4>Selected Files:</h4>
          <ul>
            {formData.files.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}
    </form>
    </div>
  );
};

export default UploadHouseForm;
