// components/UploadHouseForm.js

import React, { useState } from 'react';

const UploadHouseForm = () => {
  const [formData, setFormData] = useState({
    structureId: '',
    structureType: 'house',
    userId: '',
    images: [],
    tags: [],
    files: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData({
      ...formData,
      images: files
    });
  };

  const handleTagChange = (e) => {
    const tags = e.target.value.split(',').map(tag => tag.trim());
    setFormData({
      ...formData,
      tags: tags
    });
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData({
      ...formData,
      files: files
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send formData to backend
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="structureId">Structure ID (int):</label>
      <input
        type="number"
        id="structureId"
        name="structureId"
        value={formData.structureId}
        onChange={handleChange}
        required
      />

      <label htmlFor="userId">User ID (int):</label>
      <input
        type="number"
        id="userId"
        name="userId"
        value={formData.userId}
        onChange={handleChange}
        required
      />

      <label htmlFor="images">Images (List of images):</label>
      <input
        type="file"
        id="images"
        name="images"
        onChange={handleImageChange}
        multiple
        accept="image/*"
        required
      />

      <label htmlFor="tags">Tags (List of strings):</label>
      <input
        type="text"
        id="tags"
        name="tags"
        value={formData.tags.join(',')}
        onChange={handleTagChange}
      />

      <label htmlFor="files">Files (List of files):</label>
      <input
        type="file"
        id="files"
        name="files"
        onChange={handleFileChange}
        multiple
        required
      />

      <button type="submit">Upload</button>
    </form>
  );
};

export default UploadHouseForm;
